    private void btnConectarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConectarActionPerformed
        
        btnConectar.setEnabled(false);
        btnEnviar.setEnabled(true);
        
        //Posiciona o text area no final
        DefaultCaret caret = (DefaultCaret) txtMensagensEntrada.getCaret();
        caret.setUpdatePolicy(ALWAYS_UPDATE);
        
        String enderecoServidor = txtEnderecoServidor.getText();
        int portaServidor = Integer.parseInt(txtPortaServidor.getText());
        String nome = txtNomeUsuario.getText();
        
        //Instancia um cliente
        cliente = new Cliente(enderecoServidor, portaServidor, nome, txtMensagensEntrada);

        //Conecta o cliente
        cliente.conecta();        
    }//GEN-LAST:event_btnConectarActionPerformed

    private void btnDesconectarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDesconectarActionPerformed
        //Configura os botões
        btnConectar.setEnabled(true);
        btnEnviar.setEnabled(false);
        btnDesconectar.setEnabled(false);
        //Desconecta o cliente
        cliente.desconecta();
    }//GEN-LAST:event_btnDesconectarActionPerformed

    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        cliente.enviaMensagem(txtMensagemSaida.getText());        
    }//GEN-LAST:event_btnEnviarActionPerformed

